// for each loop
package com.javacore.demo;

public class for_each_loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int [] marks = {40,50,60};
		System.out.println("Using For Each Loop:");
		for(int a: marks)
		{
			System.out.println(a);
		}
	}

}
